<script>
window.onresize = function () {  
    let initwidth=375;
	let area=document.getElementsByTagName('area');
	let initarea = new Array(area.length-1);
	for(var i=0;i<area.length;i++){
	initarea[i]=area[i].getAttribute("coords");
	}
	let width=document.body.offsetWidth;
	let percent=width/initwidth;
	for(var i=0;i<area.length;i++){
	let coords=initarea[i];
	let arr=coords.split(",");
	for(var j=0;j<arr.length;j++){
		arr[j] *= percent;    
	}
		area[i].setAttribute("coords",arr.join(","));
	}
}
export default {
  
  data () {
    return {
        map:{
            "component-1":"前保险杠",
			"component-2":"前车盖",
			"component-3":"车顶",
			"component-4":"左前车门",
			"component-5":"左前翼子板",
			"component-6":"左后车门",
			"component-7":"左后翼子板",
			"component-8":"左后视镜",
			"component-9":"左裙边",
			"component-10":"后保险杠",
			"component-11":"后车盖",
			"component-12":"右后车门",
			"component-14":"右后翼子板",
			"component-13":"右后车镜",
			"component-15":"右前车门",
			"component-16":"右前翼子板",
			"component-17":"右裙边",
		},
		arr:[],
		mid:'',
        result:''
    }
  },
  mounted:function(){
         let initwidth=375;
		 let area=document.getElementsByTagName('area');
		 let initarea = new Array(area.length-1);
		 for(var i=0;i<area.length;i++){
			initarea[i]=area[i].getAttribute("coords");
		}
		let width=document.body.offsetWidth;
		let percent=width/initwidth;
		for(var i=0;i<area.length;i++){
    	let coords=initarea[i];
    	let arr=coords.split(",");
        for(var j=0;j<arr.length;j++){
            arr[j] *= percent;    
        }
			area[i].setAttribute("coords",arr.join(","));
		}
	
  },
  methods:{
    clickArea:function(e){
      let _this = this;
	  let comNum =  e.currentTarget.className.split(' ')[1];
	  _this.arr.push(comNum);
	  for(let i = 0;i<_this.arr.length;i++){
		for(let j = i+1; j < _this.arr.length ; j++){
			if(_this.arr[i]==_this.arr[j]){
				_this.arr.splice(i,1);
				_this.arr.splice(j-1,1);
			}
		}
	  }
	  _this.mid = '';
	  for(let index in _this.arr){
		  for(let index2 in _this.map){
			  if( _this.arr[index]==index2){
			     _this.mid +=  _this.map[index2]+' ';
		 		}
		  }
	  }
      _this.result = _this.mid;
      let doc = document.getElementsByClassName(comNum);
      for (let i = 0; i < doc.length; i++ ){
          if(doc[i].tagName == "SPAN"){
              if(doc[i].className.includes('hidden')){
                  _this.removeClass(doc[i],'hidden');
              }else{
                  _this.addClass(doc[i],'hidden');
              }
             
          }
      }
    },
    arrIndexOf:function(arr,aValue){
        for(var i=0;i<arr.length;i++){
            if(arr[i]==aValue){
                return i;
            }
        }
        return -1;
    },
    removeClass:function (obj, sClassName) {
        let _this = this;
        var arr = obj.className.split(" ");
        if (_this.arrIndexOf(arr, sClassName) != -1) {
            arr.splice(_this.arrIndexOf(arr, sClassName), 1);
            obj.className=arr.join(" ");
            }
    },
    addClass:function(obj, className) {
        let _this = this;
        if (obj.className == "") {
            obj.className = className;
        } else {
            var arr = obj.className.split(" ");
            if (_this.arrIndexOf(arr, className) == -1) {
                obj.className += " " + className;
            }
        }
	},
	//向前按钮
    forword:function(){
        let _this = this;
        let doc = document.getElementsByClassName("cv-car-view-container");
        for (let i =0;i<doc.length;i++){
            if(!doc[i].className.includes("none")){
                let active = doc[i].className.split(' ')[1].split('-')[1];
                if(active == 1){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[3],'none');
                   return;
                }
                if(active == 2){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[0],'none');
                   return;
                }
                if(active == 3){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[1],'none');
                   return;
                }
                if(active == 4){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[2],'none');
                   return;
                }
                
            }
        }

	},
	//向后按钮
    backwards:function(){
        let _this = this;
        let doc = document.getElementsByClassName("cv-car-view-container");
        for (let i =0;i<doc.length;i++){
            if(!doc[i].className.includes("none")){
                let active = doc[i].className.split(' ')[1].split('-')[1];
                if(active == 1){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[1],'none');
                   return;
                }
                if(active == 2){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[2],'none');
                   return;
                }
                if(active == 3){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[3],'none');
                   return;
                }
                if(active == 4){
                    _this.addClass(doc[i],'none');
                    _this.removeClass(doc[0],'none');
                   return;
                }
                
            }
        }
    }
}
 

}
</script>
